import csvimport loggingwith open('Trails.csv', 'r') as file:    reader = csv.reader(file)    i = 0    conditionList = {}    installationYear = {}    active = 0    hasLight = 0    walkingBiking = 0    hikingRoadList = []        for row in reader:                conditionList[row[2]] = row[9]        if i > 0 and len(row[10]) > 0:            year = int(row[10])            if year > 2000:                installationYear[row[2]] = year        if row[15].lower() == "active":            active += 1        if row[12].lower() == "yes": #light            hasLight += 1        if row[23].lower() == "yes" and row[25].lower() == "yes": #walking and biking            walkingBiking += 1                if row[24].lower() == "yes" and row[25].lower() == "yes": #hiking and biking            hikingRoadList.append((row[2],row[9],row[12], row[15],row[24],row[25],row[26]))                i = i+1        for key in conditionList:            print(key, conditionList[key])print("\n")        for key in installationYear:            print(key, installationYear[key])    print(f"\n{active} trails are currently Active")print(f"\n{hasLight} trails have lighting")print(f"\n{walkingBiking} trails are both walking and biking\n")for item in hikingRoadList:    print(item, sep=',')        #end